clc;clear all; close all;syms x;syms x; y = exp(-x) - sin(x);a = 0; b = 1;c = 5; d = 10;fplot(char(y), [a d]);f = inline(vectorize(y),'x');tol = 1e-9;x0=0;x1=0.5;x2=x0-(f(x0).*((x1-x0))/(f(x1)-f(x0)));x3=5;x4=5.75;x5=x3-(f(x3).*((x4-x3))/(f(x4)-f(x3)));k = 1;while abs(f(x2)>tol) & abs(f(x5)) > tol    x0=x1;    x1 = x2;    x2=x0-(f(x0).*((x1-x0))/(f(x1)-f(x0)));        x3=x4;    x4=x5;    x5=x3-(f(x3).*((x4-x3))/(f(x4)-f(x3)));    k=k+1;end;fprintf('Nghiem x1 = %.10f \n',x2);fprintf('Nghiem x2 = %.10f \n',x5);fprintf('So vong lap k = %d \n',k);